function row(n,o=""){return`<div class="row" style="${o}">${n}</div>`}function col(n){return`<div class="col-sm">${n}</div>`}function css(n={}){if("string"==typeof n)return n;return Object.keys(n).map(o=>`${o}: ${n[o]}`).join(";")}function block(n){return`\n  <form name="${n}">\n  <h5>${n}</h5>\n  <div class="form-group">\n    <input class="form-control form-control-sm" name="value" placeholder="value">\n  </div>\n  <div class="form-group">\n    <input class="form-control form-control-sm" name="styles" placeholder="styles">\n  </div>\n  <button type="submit" class="btn btn-primary btn-sm">Добавить</button>\n</form>\n<hr />\n   `}